import React ,{useState,useContext} from 'react'
import './booking.css'
import {Form,FormGroup,ListGroupItem,ListGroup,Button} from 'reactstrap'
import { useNavigate } from 'react-router-dom'
import {AuthContext} from '../../context/AuthContext'
import {BASE_URL} from '../../utils/config'


const Booking = ({tour,avgRating}) => {
    const {price,reviews,title} = tour
    const navigate = useNavigate()

    const {user} =useContext(AuthContext)
   
    const handleChange = async e =>{ 
        setBooking(prev=>({...prev,[e.target.id]:e.target.value}))
     
    
  }
    const serviceFee = 10
    const totalAmount = Number(price) * Number(booking.guestSize) + Number(serviceFee)
    //send data to server

    const handleClick = async e =>{
      e.preventDefault()
      console.log(booking)
      try {
        if(!user || user=== undefined){
          return alert('Please sign in')
        }
        const res = await fetch(`${BASE_URL}/booking`,{
          method:'POST',
          headers:{
            'content-type': 'application/json',
          },
          credentials:'include',
          body: JSON.stringify(booking)
        })
        const result = await res.json()
        if(!res.ok) {
          return alert(result.message)
        }
        navigate('/thank-you')
      } 
      catch (error) {
        alert(error.message)
      }
      
    
        
    }
  return (
    <div className='booking'>
      <div className="booking__top d-flex align-items-center justify-content-between">
        <h3>${price} <span>/per person</span></h3>
        <span className="tour__rating d-flex align-items-center gap-1">
          <i class="ri-star-fill"></i>{avgRating ===0? null:avgRating} ({reviews?.length})
         </span>
      </div>
      <div className="booking__form">
        <h5>Information</h5>
        <Form className="booking__info-form" onSubmit={handleClick}>
         <FormGroup>
            <input type="text" placeholder='Full names' id='fullname' required onChange={handleChange} />
         </FormGroup>
         <FormGroup>
            <input type="number" placeholder='Phone' id='phone' required onChange={handleChange} />
         </FormGroup>
         <FormGroup className='d-flex align-items-center gap-3'>
            <input type="date" placeholder='Date' id='bookAt' required onChange={handleChange} />
            <input type="number" placeholder='Guest number' id='guestSize' required onChange={handleChange} />
         </FormGroup>
        </Form>
      </div>

      <div className="booking__bottom">
        <ListGroup>
            <ListGroupItem className='border-0 px-0'>
                <h5 d-flex align-items-center gap-1>${price}
                <i class="ri-close-line"></i> 1 person
                </h5>
                <span>${price}</span>
            </ListGroupItem>
            <ListGroupItem className='border-0 px-0'>
                <h5>
                    Service Charge
                </h5>
                <span>${serviceFee}</span>
            </ListGroupItem>
            <ListGroupItem className=' total border-0 px-0'>
                <h5>
                Total
                </h5>
                <span>${totalAmount}</span>
            </ListGroupItem>
        </ListGroup>
        <Button className='btn primary__btn w-100 mt-4' onClick={handleClick}>Book Now</Button>
      </div>
    </div>
  )
}

export default Booking
